$(function(){checkLogin(),getAllAdvisory()});function getAllAdvisory(){ajaxCallGet("advisory-infor/find-all").then(b=>{console.log(b);let c=``,d=0;b.data.map(a=>{console.log(a),d++,c+=`
            <tr>
                        <td>${d}</td>
                        <td>${a.nameParents}</a> </td>
                        <td>${a.email}</td>
                        <td><span class="tag tag-success">${a.phoneNumber}</span></td>
                        <td><span>${a.nameStudent}</span></td>
                        <td><span>${a.dateOfBirthStudent}</span></td>                
                        <td><span>${a.idCourse.name}</span></td>
                        <td><span>${a.idAddressCompany.addressSchool}</span> </td>
                        <td class="d-flex justify-content-around"><input  onclick="saveAdvisory(${a.idAddressCompany.id},${a.idCourse.id},${a.id})"
                         type="checkbox" name="" value="${a.id}"></td>                                          
                    </tr>
            `}),$("#list-student").html(c)})}function saveAdvisory(a,b,c){let d=null;ajaxCallGet("advisory-infor/find-by-id/"+c).then(c=>{d=c.data,ajaxCallPost("advisory-infor/deleted/"+a+"/"+b,d).then(a=>{console.log(a),alertSuccess("B\u1EA1n \u0111\xE3 t\u01B0 v\u1EA5n th\xE0nh c\xF4ng."),setTimeout(2e3,function(){window.location.reload(!0)})}).catch(()=>{alertDanger("l\u1ED7i ch\u1EE9c n\u0103ng")})})}