window.onload=function(){let a=window.location.href.split("/"),b=a[a.length-2],c=a[0]+"//"+a[2]+"/";showDetailTinTuc(b,c),showCourseOftintuc(c)};function showDetailTinTuc(a,b){ajaxCallGet("topic/find-by-idtopic/"+a).then(a=>{let c=`
        <a href="spec.edu.vn">Trang chủ</a>
                <span class="bc"><i class="fas fa-chevron-right"></i></span>
                <a href="${b+"tintuc"}">Tin tức</a>
                <span class="bc"><i class="fas fa-chevron-right"></i></span>
                <a href="#">${a.data.title}</a>
        `;$("#urlDetailTinTuc").html(c),$("#head").append(a.data.metaHtml),$("#add-detatil-topic").html(a.data.content)}).catch(()=>{$("#add-detatil-topic").html("<span>R\u1EA5t ti\u1EBFc, n\u1ED9i dung n\xE0y kh\xF4ng t\u1ED3n t\u1EA1i.</span>")})}function showCourseOfTopic(a){let b=``;ajaxCallGet("course/find-by-id/"+a).then(a=>{let c="";c=0<a.data.percentOfPromotion?formatNumber(a.data.tuitionFeeAfterReduction,".",".")+"\u0111":formatNumber(a.data.studentFees,".",".")+"\u0111",b+=`
                        <div class="course-thumbnail">
                            <img src="${a.data.image}"
                                 alt="Lập trình di động">
<!--                                 onerror="this.onerror=null;this.src='/resources/image/thumbnail.jpg'"-->
                        </div>
                        <div  class="course-info" style="padding: 0px 10px 0px 10px">
                            <div class="course-price">

                                <div class="new-price">
                                    <span class="techmaster-price">${c}</span>đ
                                </div>
                            </div>

                            <div class="course-info-item">
                                <div class="course-info-item-i">
                                    <i class="far fa-calendar"></i>
                                </div>
                                <div class="course-info-item-content my-tooltip">
                                    ${a.data.section}
                                </div>
                            </div>

                            <div class="course-info-item">
                                <div class="course-info-item-i">
                                    <i class="far fa-money-bill-alt"></i>
                                </div>
                                <div class="course-info-item-content">
                                     <span> Hỗ trợ học phí khoá tiếp theo</span>
                                </div>
                            </div>

                        </div>
                        <div class="course-btn">
                            <a target="_blank" href="${URLHTTP+"khoa-hoc-spec/"+a.data.id+"/"+a.data.urlView}" class="btn-buy-now">
                                <button class="btn btn-red" style="width: 100%">Xem chi tiết</button>
                            </a>
                        </div>  
            `,$("#priceCourseOfTintuc").html(b)})}function showTopicLQ(a,b){let c=`<h4 class="margin-bottom-15">
                               Bài viết liên quan
                            </h4>`;ajaxCallGet("topic-has-tag/find-by-idtopic?id="+a).then(a=>{a.data.map(a=>{c+=` 

                            <div class="related-item">
                                <a href="${b}detail-topic/${a.topic.id}/${a.topic.viewUrl}" target="_blank">
                                    ${a.topic.title}
                                </a>
                            </div>
            `}),$("#showBaiVietLQ").html(c)}).catch(()=>{$("#showBaiVietLQ").html("<span>Kh\xF4ng c\xF3 b\xE0i vi\u1EBFt li\xEAn quan.</span>")})}function showCourseOftintuc(){let a=` <h4 class="margin-bottom-15"> Khoá học hay </h4>`;ajaxCallGet("course/find-by-name-image-subcontent-createdate/0").then(b=>{b.data.map(b=>{a+=` 

                            <div class="related-item">
                                <a href="${URLHTTP+"khoa-hoc-spec/"+b.id+"/"+b.urlView}" target="_blank">
                                    ${b.name}
                                </a>
                            </div>
            `}),$("#showCourseOftintuc").html(a)})}