$(function(){showTeachers()});function showTeachers(){let a=``;ajaxCallGet("teacher/find-all").then(b=>{console.log(b),b.data.map(b=>{a+=`

                    <li style="height: auto; margin-bottom: 20px">
                        <div class="c-team-item row">
                            <div class="c-team-item__avatar col-5">
                                <figure><img style="width: 100%;height: 100%" src="${b.imageSrc}" alt="avatar">
                                </figure>
                            </div>
                            <div class="c-team-item__content col-7">
                                <p class="c-team-item__name" style="margin-bottom: 0">${b.name}</p>
                                <p class="c-team-item__name" style="margin-bottom: 0">${b.regency}</p> 
                            </div>
                        </div>
                    </li>

                    
            `}),$("#showTeacher").html(a)})}